mapper.select:
select score
from rank_list
where consumerId = #{consumerId, jdbcType=BIGINT} and songListId = #{songListId, jdbcType=BIGINT};

mapper.update:
UPDATE song_list
SET score=(
  SELECT floor(AVG(score))
  FROM rank_list
  WHERE songListId=#{songListId}
)
WHERE id=#{songListId}

UPDATE song_list
SET scorecount=(
  SELECT COUNT(score)
  FROM rank_list
  WHERE songListId=#{songListId}
)
WHERE id=#{songListId}
